mapper.select:
select sum(kb.amount * kb.price)
from ka_bill kb
where create_time between #{startTime} and #{endTime}

select sum(price * amount) as consume, sum(price * amount - ka_bill.payment) as debt
from ka_bill
where user_id = #{userId}

mapper.update:
update ka_bill
user_id = #{userId,jdbcType=INTEGER}, `type` = #{type,jdbcType=VARCHAR}, specification = #{specification,jdbcType=VARCHAR}, amount = #{amount,jdbcType=INTEGER}, price = #{price,jdbcType=DECIMAL}, remark = #{remark,jdbcType=VARCHAR}, payment = #{payment,jdbcType=DECIMAL}, payment_type = #{paymentType,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP},
where id = #{id,jdbcType=INTEGER}

update ka_bill
set payment = price * amount,
payment_type = #{paymentType}
where id in

    #{id}
